import { useReccContext } from '../hooks/useReccContext';
import formatDistanceToNow from 'date-fns/formatDistanceToNow';

const ReccDetails = ({ recc }) => {

    const { dispatch } = useReccContext()

    const handleClick = async () => {
        const response = await fetch('/api/reccs/' + recc._id, {method: "PATCH"}) // Update the recommendation

        const json = await response.json()

        if(response.ok){
            dispatch({ type: 'UPDATE_RECC', payload: json })
        }
    }

    const updateRecc = async (req, res) => {

        try {
            // Find the existing document by ID
            let recc = await Recc.findById(reccId);

            if (!recc) {
              throw new Error('Recc not found'); // Handle case where recc is not found
            }

            // Update fields
            recc.ARCcode = updatedFields.ARCcode || recc.ARCcode;
            recc.location = updatedFields.location || recc.location;
            recc.description = updatedFields.description || recc.description;
            recc.template = updatedFields.template || recc.template;
            recc.reportName = updatedFields.reportName || recc.reportName;

            // Save the updated document
            await recc.save();
            console.log('Recc updated successfully');
          } catch (err) {
            console.error('Error updating recc:', err.message);
          }
        }

    return (
        <div className="recc-details">
            <h4><strong>ARC Code: </strong>{recc.ARCcode}</h4>
            <p><strong>Location: </strong>{recc.location}</p>
            <p><strong>Description: </strong>{recc.description}</p>
            <p><strong>Recommendation Template: </strong>{recc.template}</p>
            <p><strong>Report Name: </strong>{recc.reportName}</p>
            <p><strong>Last Updated: </strong>{formatDistanceToNow(new Date(recc.updatedAt), {addSuffix: true})}</p>
            <span onClick={handleClick} className="material-symbols-outlined">Delete</span>
            <span onMouseOut={


                updateRecc('6675fe2f6645774d5911e6bd', {
                    location: 'New Location',
                    description: 'Updated Description'
                  })


            } className="material-symbols-outlined">Edit</span>
        </div>
    );
}

export default ReccDetails;
