import { useEffect, useState } from 'react';
import { useReccContext } from '../hooks/useReccContext';

// Components
import ReccDetails from '../components/ReccDetails';
import ReccForm from '../components/ReccForm';
import InputBox from '../components/InputBox';

const Home = () => {
    const { routes, dispatch } = useReccContext();
    const [searchTerm, setSearchTerm] = useState('');
    const [filteredRoutes, setFilteredRoutes] = useState([]);

    useEffect(() => {
        const fetchData = async () => {
            const response = await fetch("/api/reccs/");
            const json = await response.json();

            if (response.ok) {
                dispatch({ type: 'SET_ARCcode', payload: json });
                setFilteredRoutes(json);
            }
        };
        fetchData();
    }, [dispatch]);

    const handleSearch = (event) => {
        const term = event.target.value.toLowerCase();
        setSearchTerm(term);
        if (term === '') {
            setFilteredRoutes(routes);
        } else {
            const filteredResults = routes.filter(recc =>
                recc.ARCcode.toLowerCase().includes(term) ||
                recc.location.toLowerCase().includes(term) ||
                recc.description.toLowerCase().includes(term) ||
                recc.template.toLowerCase().includes(term)
            );
            setFilteredRoutes(filteredResults);
        }
    };

    return (
        <div className="home">
            <InputBox searchTerm={searchTerm} onSearch={handleSearch} />
            <ReccForm />
            <div className="reccs">
                {filteredRoutes && filteredRoutes.map(recc => (
                    <ReccDetails recc={recc} key={recc._id} />
                ))}
            </div>
        </div>
    );
};

export default Home;
