    };

    const parsedValue = parseFloat(value); // Convert input to a floating-point number
      if (!isNaN(parsedValue) && parsedValue >= 0) { // Ensure it's a valid number and positive
        setInputValue(value);
      }
    return (
      <div>
        <input
          type="number"
          value={inputValue}
          onChange={handleChange}
          placeholder="ARC Code..."
