import { useState } from 'react';

const ReccForm = () => {
    const [ARCcode, setARCcode] = useState('');
    const [location, setLocation] = useState('');
    const [description, setDescription] = useState('');
    const [template, setTemplate] = useState('');
    const [error, setError] = useState(null);

    const handleSubmit = async (e) => {
        e.preventDefault();

        const recc = {ARCcode, location, description, template};
        console.log('Submitting recommendation:', recc); // Debugging statement

try{
        const response = await fetch('/api/reccs', {
            method: 'POST',
            body: JSON.stringify(recc),
            headers: {'Content-Type': 'application/json'}
        })
        const json = await response.json();

        if (!response.ok) {
            setError(json.error);
            console.error('Error from server:', json.error); // Debugging statement
        }
        if (response.ok) {
            setError(null);
            console.log("New recommendation added!")
            setARCcode('');
            setLocation('');
            setDescription('');
            setTemplate('');
        }}catch (error) {
            console.error('Network or server error:', error); // Catch network errors
            setError('An unexpected error occurred.');
        }}



    return (
        <form className = "create" onSubmit={handleSubmit}>
            <h3>Create a New Recommendation Template</h3>

            <label>ARC Code:</label>
            <input 
                type="number"
                required
                onChange={(e) => setARCcode(e.target.value)}
                value={ARCcode}
            />

            <label>Location:</label>
            <input 
                type="text"
                onChange={(e) => setLocation(e.target.value)}
                value={location}
            />

            <label>Description:</label>
            <input
                type="text"
                required
                onChange={(e) => setDescription(e.target.value)}
                value={description}
            />

            <label>Recommendation Template:</label>
            <input
                type="text"
                required
                onChange={(e) => setTemplate(e.target.value)}
                value={template}
            />

            <button>Submit</button>
            {error && <div className='error'>{error}</div>}

        </form>

    )}

export default ReccForm;
