// src/components/InputBox.js

import React, { useState } from 'react';

export default function InputBox({ searchTerm, onSearch }) {
    return (
        <div className='search-bar'>
            <input
                className='input-box'
                type="text"
                value={searchTerm}
                onChange={onSearch}
                placeholder="Search ARC Code..."
                style={{ margin: '20px' }}
            />
        </div>
    );
}
